import React, { useState } from "react";

import './ExpenseForm.css';

const ExpenseForm = () => {    
    //---- OTRA FORMA DE HACERLO EN UN SOLO USE STATE 
    // const [userInput, setUserInput] = useState({
    //     enteredTitle: '',
    //     enteredAmount: '',
    //     enteredDate: ''
    // });

    
    // const titleChangeHandler = (event) => {
    //     Si solo quiero cambiar el titulo, tengo que agregar el amount y el date tambien
    //     Una forma de hacerlo es poniendo "...userInput"
    //     setUserInput({
    //         ...userInput,
    //         enteredTitle: event.target.value
    //     });
    // }

    // const amountChangeHandler = (event) => {
    //     setUserInput({
    //         ...userInput,
    //         enteredAmount: event.target.value
    //     });
    // }

    // const dateChangeHandler = (event) => {
    //     setUserInput({
    //         ...userInput,
    //         enteredDate: event.target.value
    //     });
    // }

    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value);
    }

    const amountChangeHandler = (event) => {
        setEnteredAmount(event.target.value);
    }

    const dateChangeHandler = (event) => {
        setEnteredDate(event.target.value);
    }

    return (
       <form>
        <div className="new-expense__controls"></div>
        <div className="new-expense__control">
            <label>Title</label>
            <input type="text" onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
            <label>Amount</label>
            <input type="number" min="0.01" step="0.01" onChange={amountChangeHandler} />
        </div>
        <div className="new-expense__control">
            <label>Date</label>
            <input type="date" min="2019-01-01" max="2022-12-31" onChange={dateChangeHandler}/>
        </div>
        <div className="new-expense__actions">
            <button type="submit">Add Expense</button>
        </div>
       </form>
    );
}

export default ExpenseForm;
