import React, { useState } from "react";

import './ExpenseForm.css';

const ExpenseForm = () => {    
    //--------------------- OTRA FORMA DE HACERLO CON UN SOLO USE STATE ---------------------
    // const [userInput, setUserInput] = useState({
    //     enteredTitle: '',
    //     enteredAmount: '',
    //     enteredDate: ''
    // });
    
